package org.opentutorials.javatutorialssecond.loop;

/**
 * @author user
 *
 */
public class LoopDepthDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				System.out.println("i = " + i + ", " + "j = " + j);
			}
		}

	}

}
