package org.opentutorials.javatutorialssecond.method;

/**
 * @author user
 *
 */
public class MethodDemo2 {

	/**
	 * @param args
	 */

	public static void numbering() {
		for (int i = 0; i < 10; i++) {
			System.out.println(i);
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i < 5; i++) {
			numbering();
		}

	}

}
