public class MethodDemo4 {

	/**
	 * @param args
	 */

	public static void numbering(int limit) {
		for (int i = 0; i < limit; i++) {
			System.out.println(i);
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		numbering(5);
		numbering(2);
	}

}
